import React from "react";
import useFetch from "../../hooks/useFetch";
import Loading from "../../components/Global/Loading/Loading";
import ProductCard from "../../components/Global/Card/productCard";
import { useState } from "react";

const Catalogus = (product) => {
  const { isLoading, error, data: catalogus } = useFetch("/catalogus");

  const [isInWishlist, setIsInWishlist] = useState(
    localStorage.getItem(product._id) === "true"
  );

  const handleAddToWishlist = () => {
    setIsInWishlist(true);
    localStorage.setItem(product._id, "true");
  };

  if (error) {
    return <p>{error}</p>;
  }

  if (isLoading) {
    return <Loading />;
  }

  return (
    <div>
      <h2>Catalogus</h2>
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 row-cols-xl-4 g-4">
        {catalogus.map((product) => (
          <div className="col" key={product._id}>
            <ProductCard product={product} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Catalogus;
