import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Loading from "../../components/Global/Loading/Loading";
import useFetch from "../../hooks/useFetch";

const WishlistItem = (product) => {
  // const [product, setProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAdded, setIsAdded] = useState(false);

  useEffect(() => {
    // Check if the item is in local storage and update isAdded state
    const storedItem = localStorage.getItem(product.itemId);
    setIsAdded(!!storedItem);
  }, [itemId, product._id, product.itemId]);

  const toggleWishlist = () => {
    if (isAdded) {
      // Remove the item from local storage
      localStorage.removeItem(itemId);
    } else {
      // Add the item to local storage
      localStorage.setItem(itemId, "true");
    }
    setIsAdded(!isAdded);
  };

  if (error) {
    return <p>{error}</p>;
  }

  if (isLoading) {
    return <Loading />;
  }

  return (
    <div>
      <h3>{product.name}</h3>
      <p>â‚¬{product.price}</p>
      <button onClick={toggleWishlist}>
        {isAdded ? "Remove from Wishlist" : "Add to Wishlist"}
      </button>
    </div>
  );
};

export default WishlistItem;
