import React, { useEffect, useState } from "react";

const WishlistCounter = () => {
  const [wishlistCount, setWishlistCount] = useState(0);

  useEffect(() => {
    // Retrieve wishlist items from localStorage and calculate the count
    const keys = Object.keys(localStorage);
    const count = keys.filter(
      (key) => localStorage.getItem(key) === "true"
    ).length;
    setWishlistCount(count);
  }, []);

  return <div>Wishlist Count: {wishlistCount}</div>;
};

export default WishlistCounter;
