import React from "react";
import CouponCard from "../../components/Global/Coupon/couponCard";
import ProductCardAdmin from "../../components/Global/Card/ProductCardAdmin";
import UserCard from "../../components/Global/Card/UserCard";
import Loading from "../../components/Global/Loading/Loading";
import useFetch from "../../hooks/useFetch";
import { Link } from "react-router-dom";
import Button from "../../components/Global/Button/Button";

const Admin = () => {
  const { isLoading, error, data: coupon } = useFetch("/coupons");
  const {
    isLoading: isLoadingProducts,
    error: errorProducts,
    data: products,
  } = useFetch("/catalogus");
  const {
    isLoading: isLoadingUser,
    error: errorUser,
    data: user,
  } = useFetch("/users");

  if (error) {
    return <p>{error}</p>;
  }

  if (isLoading) {
    return <Loading />;
  }

  if (errorProducts) {
    return <p>{errorProducts}</p>;
  }

  if (isLoadingProducts) {
    return <Loading />;
  }

  return (
    <div>
      <h1>Admin page</h1>
      <p>Welkom op de administrator page</p>
      <div className="users">
        <h2>Users</h2>
        <UserCard user={user} />
      </div>
      <div className="products">
        <h2>Producten</h2>
        <Link to={"addProduct"}>
          <Button> Add product</Button>
        </Link>
        <div className="row">
          {products &&
            products.map((product) => (
              <div className="col-md-4" key={product._id}>
                <ProductCardAdmin product={product} />
              </div>
            ))}
        </div>
      </div>
      <div className="couponcodes">
        <h2>Coupon codes</h2>
        <Link to={"addCoupon"}>
          <Button> Add coupon code</Button>
        </Link>
        <div className="row">
          {coupon &&
            coupon.map((coupon) => (
              <div className="col-md-4" key={coupon._id}>
                <CouponCard coupon={coupon} />
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default Admin;
