import { Link, useNavigate } from "react-router-dom";
import useMutation from "../../../hooks/useMutation";
import { useState } from "react";

const EditUser = ({ user, onUpdate }) => {
  const navigate = useNavigate();
  const { isLoading, error, mutate } = useMutation();
  const [userName, setUserName] = useState("");
  const [userEmail, setUserEmail] = useState("");
  const [userStreet, setUserStreet] = useState("");
  const [userCity, setUserCity] = useState("");
  const [userPostalCode, setUserPostalCode] = useState("");
  const [userStreetNumber, setUserStreetNumber] = useState("");
  const [userRole, setUserRole] = useState("");

  const userFromLocalStorage = JSON.parse(localStorage.getItem("user"));
  const userId = userFromLocalStorage._id;
  const initialUserName = userFromLocalStorage.username;
  const initialUserEmail = userFromLocalStorage.email;
  const initialUserStreet = userFromLocalStorage.address_street;
  const initialUserCity = userFromLocalStorage.address_city;
  const initialUserPostalCode = userFromLocalStorage.address_postalcode;
  const initialUserStreetNumber = userFromLocalStorage.address_number;
  const initialUserRole = userFromLocalStorage.role;

  const handleUserChange = (event) => {
    const { id, value } = event.target;
    if (id === "userName") {
      setUserName(value);
    } else if (id === "userEmail") {
      setUserEmail(value);
    } else if (id === "userStreet") {
      setUserStreet(value);
    } else if (id === "userCity") {
      setUserCity(value);
    } else if (id === "userPostalCode") {
      setUserPostalCode(value);
    } else if (id === "userStreetNumber") {
      setUserStreetNumber(value);
    } else if (id === "userRole") {
      setUserRole(value);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const data = {
        userName: userName,
        email: userEmail,
        street: userStreet,
        city: userCity,
        postalCode: userPostalCode,
        streetNumber: userStreetNumber,
        // Add other fields you want to update here
      };

      const response = await mutate(
        `${process.env.REACT_APP_API_URL}/users/${userId}`, // Assuming this is the endpoint to update a user
        {
          method: "PATCH",
          data: data,
          onSuccess: () => {
            onUpdate();
            navigate(`/admin`);
          },
        }
      );

      if (response && response.status === 200) {
        const responseData = response.data;
        console.log("User edited successfully:", responseData);
      } else {
        console.error("Failed to edit user. Server response:", response);
      }

      // Clear form fields and any other necessary actions
      setUserName("");
      setUserEmail("");
      setUserStreet("");
      setUserCity("");
      setUserPostalCode("");
      setUserStreetNumber("");
      setUserRole("");

      // Redirect or perform necessary actions after editing
      // window.location.href = "../"; // You might want to update this
    } catch (error) {
      console.error("Error editing user:", error);
    }
  };

  return (
    <div>
      <Link to="/admin">&lt; Back</Link>
      <h3>Edit User</h3>

      {error && <p>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="userName">User name:</label>
          <input
            type="text"
            className="form-control"
            id="userName"
            value={userName}
            onChange={handleUserChange}
            placeholder={initialUserName}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="userEmail">User Email:</label>
          <input
            type="email"
            className="form-control"
            id="userEmail"
            value={userEmail}
            onChange={handleUserChange}
            placeholder={initialUserEmail}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="userStreet">Street:</label>
          <input
            type="text"
            className="form-control"
            id="userStreet"
            value={userStreet}
            onChange={handleUserChange}
            placeholder={initialUserStreet}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="userCity">City:</label>
          <input
            type="text"
            className="form-control"
            id="userCity"
            value={userCity}
            onChange={handleUserChange}
            placeholder={initialUserCity}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="userPostalCode">Postal Code:</label>
          <input
            type="text"
            className="form-control"
            id="userPostalCode"
            value={userPostalCode}
            onChange={handleUserChange}
            placeholder={initialUserPostalCode}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="userStreetNumber">Street Number:</label>
          <input
            type="text"
            className="form-control"
            id="userStreetNumber"
            value={userStreetNumber}
            onChange={handleUserChange}
            placeholder={initialUserStreetNumber}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="userRole">Role:</label>
          <div>
            <label>
              <input
                type="radio"
                id="userRole"
                value="admin"
                checked={userRole === "admin"}
                onChange={handleUserChange}
                required
              />{" "}
              Admin
            </label>
            <label>
              <input
                type="radio"
                id="userRole"
                value="gebruiker"
                checked={userRole === "gebruiker"}
                onChange={handleUserChange}
                required
              />{" "}
              Gebruiker
            </label>
          </div>
        </div>
        <button type="submit" className="btn btn-primary">
          Edit User
        </button>
      </form>
    </div>
  );
};

export default EditUser;
