import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Loading from "../../components/Global/Loading/Loading";
import useFetch from "../../hooks/useFetch";
import Button from "../../components/Global/Button/Button";
import style from "./catalogusDetail.module.css";

const ProductDetail = () => {
  const { id } = useParams();
  const {
    isLoading,
    error,
    invalidate,
    data: product,
  } = useFetch(`/catalogus/${id}`);
  const [isInWishlist, setIsInWishlist] = useState(
    localStorage.getItem(product.id) === "true"
  );

  if (error) {
    return <p>{error}</p>;
  }

  if (isLoading) {
    return <Loading />;
  }

  const handleToggleWishlist = () => {
    if (isInWishlist) {
      localStorage.removeItem(product._id);
    } else {
      localStorage.setItem(product._id, "true");
    }
    setIsInWishlist(!isInWishlist);
  };

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-12">
          <Link to="/catalogus" className="text-muted">
            &lt; Back
          </Link>
          <h2 className="mt-3">{product.name}</h2>
        </div>
      </div>
      <div className="row mt-4">
        <div className="col-md-6 position-relative">
          <button
            onClick={handleToggleWishlist}
            className={`btn btn-link ${
              isInWishlist ? style.wishlistActive : ""
            }`}
          >
            <i
              className={`fa-heart ${
                isInWishlist ? "fas" : "far"
              } text-danger ${style.wishlistIcon}`}
            ></i>{" "}
          </button>
          <img
            src={`../images/${product.photo}`}
            alt={product.name}
            className="img-fluid"
          />
        </div>
        <div className="col-md-6">
          <h4>Description:</h4>
          <p>{product.description}</p>
          <h4>Price:</h4>
          <p>â‚¬{product.price}</p>
          <Button className="btn btn-primary">Add to Cart</Button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
