import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Loading from "../../components/Global/Loading/Loading";
import useFetch from "../../hooks/useFetch";

const WishlistItem = (itemId) => {
  const [isAdded, setIsAdded] = useState(false);
  console.log(itemId);

  // Fetch product data from api
  const { isLoading, error, data: product } = useFetch("/catalogus/" + itemId);
  console.log(product);

  useEffect(() => {
    // Check if the item is in local storage and update isAdded state
    const storedItem = localStorage.getItem(itemId);
    setIsAdded(!!storedItem);
  }, [itemId]);

  if (error) {
    return <p>{error}</p>;
  }
  if (isLoading) {
    return <Loading />;
  }

  return (
    <div>
      <h3>{product.name}</h3>
      <p>â‚¬{product.price}</p>
    </div>
  );
};

export default WishlistItem;
