import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Loading from "../../components/Global/Loading/Loading";

const WishlistItem = (itemId) => {
  const [product, setProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAdded, setIsAdded] = useState(false);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`/catalogus/${itemId}`);
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const productData = await response.json();
        setProduct(productData);
        setIsLoading(false);
      } catch (err) {
        setError(err.message);
        setIsLoading(false);
      }
    };

    fetchProduct();
  }, [itemId]);

  useEffect(() => {
    // Check if the item is in local storage and update isAdded state
    const storedItem = localStorage.getItem(itemId);
    setIsAdded(!!storedItem);
  }, [itemId]);

  const toggleWishlist = () => {
    if (isAdded) {
      // Remove the item from local storage
      localStorage.removeItem(itemId);
    } else {
      // Add the item to local storage
      localStorage.setItem(itemId, "true");
    }
    setIsAdded(!isAdded);
  };

  if (error) {
    return <p>{error}</p>;
  }

  if (isLoading) {
    return <Loading />;
  }

  return (
    <div>
      <h3>{product.name}</h3>
      <p>â‚¬{product.price}</p>
      <button onClick={toggleWishlist}>
        {isAdded ? "Remove from Wishlist" : "Add to Wishlist"}
      </button>
    </div>
  );
};

export default WishlistItem;
