import { Link, useNavigate } from "react-router-dom";
import useMutation from "../../../hooks/useMutation";
import { useState } from "react";

const EditProduct = ({ product, onUpdate }) => {
  console.log("working");
  const navigate = useNavigate();
  const { isLoading, error, mutate } = useMutation();
  const [productName, setProductName] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productPhoto, setProductPhoto] = useState(null);

  const productId = product._id;
  const initialProductName = product.productName;
  const initialProductPrice = product.price;
  const initialProductDescription = product.description;
  const initialProductPhoto = product.photo;

  const handleProductChange = (event) => {
    const { id, value } = event.target;
    if (id === "productName") {
      setProductName(value);
    } else if (id === "productPrice") {
      setProductPrice(value);
    } else if (id === "productDescription") {
      setProductDescription(value);
    } else if (id === "productPhoto") {
      setProductPhoto(event.target.files[0]);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const data = {
        productName,
        price: productPrice,
        description: productDescription,
        photo: productPhoto,
      };

      const response = await mutate(
        `${process.env.REACT_APP_API_URL}/product/${productId}`,
        {
          method: "PATCH",
          data: data,
          onSuccess: () => {
            onUpdate();
            navigate(`/admin`);
          },
        }
      );

      if (response && response.status === 200) {
        const responseData = response.data;
        console.log("Product edited successfully:", responseData);
      } else {
        console.error("Failed to edit product. Server response:", response);
      }

      setProductName("");
      setProductPrice("");
      setProductDescription("");
      setProductPhoto(null);

      window.location.href = "../";
    } catch (error) {
      console.error("Error editing product:", error);
    }
  };

