import React from "react";
import CouponCard from "../../components/Global/Coupon/couponCard";
import ProductCardAdmin from "../../components/Global/Card/ProductCardAdmin";
import Loading from "../../components/Global/Loading/Loading";
import useFetch from "../../hooks/useFetch";
import { Link } from "react-router-dom";
import Button from "../../components/Global/Button/Button";

const Admin = () => {
  const { isLoading, error, data: coupon } = useFetch("/coupons");
  const { isLoading2, error2, data: products } = useFetch("/catalogus");

  if (error) {
    return <p>{error}</p>;
  }

  if (isLoading) {
    return <Loading />;
  }

  if (error2) {
    return <p>{error}</p>;
  }

  if (isLoading2) {
    return <Loading />;
  }

  return (
    <div>
      <h1>Admin page</h1>
      <p>Welkom op de administrator page</p>
      <div className="users">
        <h2>Users</h2>
        {/* <ProductCard key={_id} /> */}
      </div>
      <div className="products">
        <h2>Producten</h2>
        {products.map((product) => (
          <div className="col-md-4" key={product._id}>
            {/* Use col-md-4 (or any other desired column class) to control the width of each product card */}
            <ProductCardAdmin product={product} />
          </div>
        ))}
      </div>
      <div className="couponcodes">
        <h2>Coupon codes</h2>
        <Link to={"addCoupon"}>
          <Button> Add coupon code</Button>
        </Link>
        <div className="row">
          {coupon.map((coupon) => (
            <div className="col-md-4" key={coupon._id}>
              {/* Use col-md-4 (or any other desired column class) to control the width of each coupon card */}
              <CouponCard coupon={coupon} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Admin;
