import React, { useEffect, useState } from "react";
import ProductCard from "../../components/Global/Card/productCard";

const Checkout = () => {
  // get user info from local storage
  const userInfo = JSON.parse(localStorage.getItem("user")) || {};
  const [firstName, setFirstName] = useState();
  const [lastName, setLastName] = useState();
  const [street, setStreet] = useState();
  const [houseNumber, setHouseNumber] = useState();
  const [postalCode, setPostalCode] = useState();
  const [city, setCity] = useState();
  const [country, setCountry] = useState();
  const [email, setEmail] = useState();
  const [phone, setPhone] = useState();
  const [cartItems, setCartItems] = useState([]);
  // const [loading, setLoading] = useState(true);

  const initialUserName = userInfo.username;
  const initialStreet = userInfo.address_street;
  const initialHouseNumber = userInfo.address_number;
  const initialPostalCode = userInfo.address_postalcode;
  const initialCity = userInfo.address_city;
  const initialPhone = userInfo.phone;
  const initialEmail = userInfo.email;

  useEffect(() => {
    const order = JSON.parse(localStorage.getItem("cart")) || [];
    setCartItems(order);
  }, []);

  const handleCheckoutChange = (event) => {
    const { id, value } = event.target;
    if (id === "firstName") {
      setFirstName(value);
    } else if (id === "lastName") {
      setLastName(value);
    } else if (id === "street") {
      setStreet(value);
    } else if (id === "houseNumber") {
      setHouseNumber(value);
    } else if (id === "postalCode") {
      setPostalCode(value);
    } else if (id === "city") {
      setCity(value);
    } else if (id === "country") {
      setCountry(value);
    } else if (id === "email") {
      setEmail(value);
    } else if (id === "phone") {
      setPhone(value);
    } else {
      console.log("No id found");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const data = {
        username: initialUserName,
        firstName: firstName,
        lastName: lastName,
        street: street,
        houseNumber: houseNumber,
        postalCode: postalCode,
        city: city,
        country: country,
        email: email,
        phone: phone,
        // order: JSON.parse(localStorage.getItem("cart")),
      };

      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/checkout`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(data),
        }
      );

      if (response && response.status === 201) {
        // The request was successful
        // You can access the response data if needed
        const responseData = await response.json();
        console.log("Order created successfully:", responseData);

        // go to order confirmation page
        window.location.href = "../order-confirmation";
      } else {
        // The server returned an error status
        console.error("Failed to create order. Server response:", response);
        // Handle the error appropriately, such as displaying an error message.
      }
      setFirstName("");
      setLastName("");
      setStreet("");
      setHouseNumber("");
      setPostalCode("");
      setCity("");
      setCountry("");
      setEmail("");
      setPhone("");
      localStorage.removeItem("cart");
    } catch (error) {
      console.error("Failed to create order:", error);
      // Handle the error appropriately, such as displaying an error message.
    }
  };

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <div className="order-items">
        <h3>Order items</h3>
        {cartItems.length === 0 ? (
          <p>No items in cart</p>
        ) : (
          cartItems.map((item) => (
            <div className="checkout-item" key={item.id}>
              <p>{item.name}</p>
              <p>â‚¬{item.price}</p>
            </div>
          ))
        )}
      </div>
      <div className="form">
        <p>Checkout for: {initialUserName}</p>
        <div className="checkout-form">
          <form>
            <div className="form-group">
              <label htmlFor="firstName">First Name</label>
              <input
                type="text"
                className="form-control"
                id="firstName"
                placeholder="First Name"
                onChange={handleCheckoutChange}
                value={firstName}
              />
            </div>
            <div className="form-group">
              <label htmlFor="lastName">Last Name</label>
              <input
                type="text"
                className="form-control"
                id="lastName"
                placeholder="Last Name"
                onChange={handleCheckoutChange}
                value={lastName}
              />
            </div>
            <div className="form-group">
              <label htmlFor="street">Street</label>
              <input
                type="text"
                className="form-control"
                id="street"
                placeholder="Street"
                value={initialStreet}
                onChange={handleCheckoutChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="houseNumber">House Number</label>
              <input
                type="text"
                className="form-control"
                id="houseNumber"
                placeholder="House Number"
                value={initialHouseNumber}
                onChange={handleCheckoutChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="postalCode">Postal Code</label>
              <input
                type="text"
                className="form-control"
                id="postalCode"
                placeholder="Postal Code"
                value={initialPostalCode}
                onChange={handleCheckoutChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="city">City</label>
              <input
                type="text"
                className="form-control"
                id="city"
                placeholder="City"
                value={initialCity}
                onChange={handleCheckoutChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="country">Country</label>
              <input
                type="text"
                className="form-control"
                id="country"
                placeholder="Country"
                onChange={handleCheckoutChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="phoneNumber">Phone Number</label>
              <input
                type="text"
                className="form-control"
                id="phoneNumber"
                placeholder="Phone Number"
                value={initialPhone}
                onChange={handleCheckoutChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input
                type="text"
                className="form-control"
                id="email"
                placeholder="Email Address"
                value={initialEmail}
                onChange={handleCheckoutChange}
              />
            </div>
            <button
              type="submit"
              className="btn btn-primary"
              onClick={handleSubmit}
            >
              Order!
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
