import React, { useState, useEffect } from "react";

const WishlistItem = ({ itemId }) => {
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Simulate fetching item details based on itemId from an API
    // For demonstration purposes, using a setTimeout to mimic an async fetch
    const fetchProductDetails = async () => {
      // Replace this with your actual API call or data retrieval logic
      // For now, let's assume you have a list of products with their details
      const response = await fetch(`/products/${itemId}`);
      const data = await response.json();
      setProduct(data); // Set fetched product details in state
    };

    fetchProductDetails();
  }, [itemId]);

  if (!product) {
    return <p>Loading...</p>;
  }

  return (
    <li>
      <h3>{product.name}</h3>
      <p>â‚¬{product.price}</p>
      {/* Add more details or actions here */}
    </li>
  );
};

export default WishlistItem;
