import React, { useEffect, useState } from "react";

const WishlistCounter = () => {
  const [wishlistCount, setWishlistCount] = useState(0);

  useEffect(() => {
    // Function to update the wishlist count based on local storage
    const updateWishlistCount = () => {
      const keys = Object.keys(localStorage);
      const count = keys.filter(
        (key) => localStorage.getItem(key) === "true"
      ).length;
      setWishlistCount(count);
    };

    // Initial update
    updateWishlistCount();

    // Listen for changes in local storage
    window.addEventListener("storage", updateWishlistCount);

    // Cleanup
    return () => {
      window.removeEventListener("storage", updateWishlistCount);
    };
  }, []);

  return <div>Wishlist Count: {wishlistCount}</div>;
};

export default WishlistCounter;
