import React, { useState } from "react";

const Checkout = () => {
  // get user info from local storage
  const userInfo = JSON.parse(localStorage.getItem("user")) || {};
  const [userName, setUserName] = useState();
  const [firstName, setFirstName] = useState();
  const [lastName, setLastName] = useState();
  const [street, setStreet] = useState();
  const [houseNumber, setHouseNumber] = useState();
  const [postalCode, setPostalCode] = useState();
  const [city, setCity] = useState();
  const [country, setCountry] = useState();
  const [email, setEmail] = useState();
  const [phone, setPhone] = useState();
  // const [loading, setLoading] = useState(true);

  const initialUserName = userInfo.username;

  const handleCheckoutChange = (event) => {
    const { id, value } = event.target;
    if (id === "firstName") {
      setFirstName(value);
    } else if (id === "lastName") {
      setLastName(value);
    } else if (id === "street") {
      setStreet(value);
    } else if (id === "houseNumber") {
      setHouseNumber(value);
    } else if (id === "postalCode") {
      setPostalCode(value);
    } else if (id === "city") {
      setCity(value);
    } else if (id === "country") {
      setCountry(value);
    } else if (id === "email") {
      setEmail(value);
    } else if (id === "phone") {
      setPhone(value);
    } else {
      console.log("No id found");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const data = {
        username: initialUserName,
        firstName: firstName,
        lastName: lastName,
        street: street,
        houseNumber: houseNumber,
        postalCode: postalCode,
        city: city,
        country: country,
        email: email,
        phone: phone,
        order: JSON.parse(localStorage.getItem("cart")),
      };

      const response = await fetch(`${process.env.REACT_APP_API_URL}/orders`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (response && response.status === 201) {
        // The request was successful
        // You can access the response data if needed
        const responseData = await response.json();
        console.log("Order created successfully:", responseData);

        // Perform any additional actions based on the response
        // For example, show a success message or update the state with the response data.
      } else {
        // The server returned an error status
        console.error("Failed to create order. Server response:", response);
        // Handle the error appropriately, such as displaying an error message.
      }
      setFirstName("");
      setLastName("");
      setStreet("");
      setHouseNumber("");
      setPostalCode("");
      setCity("");
      setCountry("");
      setEmail("");
      setPhone("");
      localStorage.removeItem("cart");
    } catch (error) {
      console.error("Failed to create order:", error);
      // Handle the error appropriately, such as displaying an error message.
    }
  };

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <p>Checkout for: {initialUserName}</p>
      <div className="checkout-form">
        <form>
          <div className="form-group">
            <label htmlFor="firstName">First Name</label>
            <input
              type="text"
              className="form-control"
              id="firstName"
              placeholder="First Name"
            />
          </div>
          <div className="form-group">
            <label htmlFor="lastName">Last Name</label>
            <input
              type="text"
              className="form-control"
              id="lastName"
              placeholder="Last Name"
              value={lastName}
            />
          </div>
          <div className="form-group">
            <label htmlFor="street">Street</label>
            <input
              type="text"
              className="form-control"
              id="street"
              placeholder="Street"
            />
          </div>
          <div className="form-group">
            <label htmlFor="houseNumber">House Number</label>
            <input
              type="text"
              className="form-control"
              id="houseNumber"
              placeholder="House Number"
            />
          </div>
          <div className="form-group">
            <label htmlFor="postalCode">Postal Code</label>
            <input
              type="text"
              className="form-control"
              id="postalCode"
              placeholder="Postal Code"
            />
          </div>
          <div className="form-group">
            <label htmlFor="city">City</label>
            <input
              type="text"
              className="form-control"
              id="city"
              placeholder="City"
            />
          </div>
          <div className="form-group">
            <label htmlFor="country">Country</label>
            <input
              type="text"
              className="form-control"
              id="country"
              placeholder="Country"
            />
          </div>
          <div className="form-group">
            <label htmlFor="phoneNumber">Phone Number</label>
            <input
              type="text"
              className="form-control"
              id="phoneNumber"
              placeholder="Phone Number"
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="text"
              className="form-control"
              id="email"
              placeholder="Email Address"
            />
          </div>
          <div className="form-group">
            <label htmlFor="paymentMethod">Payment Method</label>
            <select className="form-control" id="paymentMethod">
              <option>Credit Card</option>
              <option>PayPal</option>
              <option>Bank Transfer</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="creditCardNumber">Credit Card Number</label>
            <input
              type="text"
              className="form-control"
              id="creditCardNumber"
              placeholder="Credit Card Number"
            />
          </div>
          <div className="form-group">
            <label htmlFor="creditCardExpirationDate">
              Credit Card Expiration Date
            </label>
            <input
              type="text"
              className="form-control"
              id="creditCardExpirationDate"
              placeholder="Credit Card Expiration Date"
            />
          </div>
          <div className="form-group">
            <label htmlFor="creditCardSecurityCode">
              Credit Card Security Code
            </label>
            <input
              type="text"
              className="form-control"
              id="creditCardSecurityCode"
              placeholder="Credit Card Security Code"
            />
          </div>
        </form>
      </div>
    </div>
  );
};

export default Checkout;
